import React, { Component } from 'react'

import './css/index.css'

export default class SingbieBox extends Component {

  singleBoxFocus = (index) => {
    return () => {
      this[index].className = 'single-box-foucs'
      console.log(this[index].className);
    }
  }

  render() {
    let date = this.props.data || this.props.data1 ||
      this.props.data2 || this.props.data3 || this.props.data4
    return (
      <div className="single-box">
        {
          date.map((value, index) => {
            return <div className='' value={value} onClick={this.singleBoxFocus(index)} ref={e => this[index] = e} key={index}  ><span></span><a onClick={event => event.preventDefault()} href='/#'>{value}</a></div>
          })
        }

      </div>
    )
  }
}
