import React, { Component } from 'react'
// import { classDetail } from '../../../redux/actions/classSynopsis'
// import store from '../../../redux/store'
// import PubSub from 'pubsub-js'
// import PubSub from 'pubsub-js'
// import ReactDOM from 'react-dom'

import './css/index.css'
export default class ClassSynopsis extends Component {

  showClassMenber = () => {
    console.log(this.props);
    const { classDetail, parents, students, teachers } = this.props
    const data = { parents, students, teachers }
    // console.log(data);
    // console.log(this.props);
    // console.log(document.getElementsByClassName('class-bar'));
    // ReactDOM.unmountComponentAtNode(document.getElementsByClassName('class-bar')[0])
    // PubSub.publish('classMessage', this.props)
    // PubSub.publish('classMessage', this.props)
    classDetail(data)
    // console.log(this.props);
    // console.log(store.getState());
  }

  render() {
    // console.log(this.props);
    const { section, grade, headmaster, students } = this.props
    return (
      <div onClick={this.showClassMenber} className="class-details">
        <div className='course-logo'></div>
        <div className="class-detail">
          <p>{section}</p>
          <p><i>年级：</i><span>{grade}</span></p>
          <p><i>班主任：</i><span style={{ fontWeight: 'bold' }}>{headmaster}</span></p>
          <p><i>学生：</i><span>{students.length || 0}人</span></p>、
        </div>
        <button className='class-details-btn'>行政班</button>
      </div>
    )
  }
}
