import React, { Component } from 'react'
import PubSub from 'pubsub-js'

import './css/index.css'

export default class SearchTeacher extends Component {

  chooceCourse = (e) => {
    console.log(e.target.childNodes[0]);
    const str = e.target.childNodes[0]
    const reg =
      str = str.replace(/^"|"$/g, "")
    console.log(this);
    // const parentsArr = parents.filter(array => JSON.stringify(array).indexOf(this.serachInpt.value) > -1)
    PubSub.publish('chooceCourse', e.target.childNodes[0])
  }


  render() {
    // console.log(this.props);
    const { props: { headmaster }, chooceCourse } = this
    return (
      <div>
        {/* 科任老师筛选框 */}
        <div className="detail-message-nav ">
          <span className='headmaster' onClick={chooceCourse}>班主任</span>
          <span onClick={chooceCourse}>生物老师</span>
          <span onClick={chooceCourse}>语文</span>
          <span onClick={chooceCourse}>信息与技术</span>
          <span onClick={chooceCourse}>数学</span>
          <span onClick={chooceCourse}>体育</span>
          <span onClick={chooceCourse}>美术</span>
          <span onClick={chooceCourse}>语文</span>
          <span onClick={chooceCourse}>信息与技术</span>
          <div className='teachename'><i>{headmaster}</i></div>
        </div>
      </div>
    )
  }
}
