import React, { Component } from 'react'
import PubSub from 'pubsub-js'

import {
  addClass,
  deleClass,
} from '../../../../func/toggleClass'
import BackClassBtn from '../../../../components/content/BackClassBtn'
import SingleBox from '../../../../containers/SingleBox'
import DropDowm from '../../../../containers/DropDowm'
import './css/index.css'

export default class CreateClass extends Component {

  state = {
    newClass: { students: 0, headmaster: '田小雨', },
  }

  schoolRef = React.createRef()
  schoolChildRef = React.createRef()
  classRef = React.createRef()
  classChildRef = React.createRef()



  // school 失去焦点的回调
  loseFocus = () => {
    // 解构赋值
    const {
      schoolRef,
      schoolRef: { current: { value } },
      schoolChildRef,
      schoolRef: { current: schoolCurrent },
      props: { setSchoolValue }
    } = this

    const reg = new RegExp('^[\u4e00-\u9fa5 ]{4,20}$|^[A-z ]{5,50}$', 'g')
    let str = value
    // 去除中间多余的空格
    str = str.replace(/\s{2,}/g, " ")
    // 去除首尾的空格
    str = str.replace(/^\s*|\s*$/g, "")
    schoolRef.current.value = str

    // console.log(schoolRef.current.value);
    const reslt = reg.test(str)
    if (!Boolean(reslt)) {
      deleClass(schoolChildRef.current, 'school-true-tips')
      deleClass(schoolChildRef.current, 'school-tips-false')
    } else {
      deleClass(schoolChildRef.current, 'school-tips-false')
      addClass(schoolChildRef.current, 'school-true-tips')

      // 
      setSchoolValue({ school: schoolCurrent.value })

    }
  }
  //class-name 失去焦点的回调
  loseClassFocus = () => {
    const {
      classRef, classRef: { current: { value } },
      classChildRef,
      //  addClass, deleClass,
      classRef: { current: classCurrent },
      // state: { newClass }
      props: { setClassValue }
    } = this
    const reg = /^[\u4e00-\u9fa5A-z ]{1,20}$/g
    let str = value

    // 去除中间多余的空格
    str = str.replace(/\s{2,}/g, " ")
    // 去除首尾的空格
    str = str.replace(/^\s*|\s*$/g, "")
    //去除_和%
    str = str.replace(/[_%]/g, '')
    classRef.current.value = str
    // console.log(classRef.current.value);
    const reslt = reg.test(str)
    if (!reslt) {
      deleClass(classChildRef.current, 'course-tips-true')
      deleClass(classChildRef.current, 'course-tips-false')
    } else {
      deleClass(classChildRef.current, 'course-tips-false')
      addClass(classChildRef.current, 'course-tips-true')
      // const setClass = { ...newClass, course: classCurrent.value }
      // this.setState({ newClass: setClass }, () => {
      //   console.log(this.state);
      // })
      setClassValue({ course: classCurrent.value })
    }
  }

  // 点击保存按钮的回调
  saveData = (e) => {
    // 取消按钮默认点击事件
    e.preventDefault()
    // console.log(this.props);
    // 连续结构赋值和重命名
    const { props: { createClass, dropDowm, singleBox }, state: { newClass } } = this
    // const { schoolRef: { current: schoolCurrent }, classRef: { current: classCurrent } } = this
    // console.log(schoolCurrent.value);
    // console.log(classCurrent.value);

    // 生成id
    // const classId = nanoid()
    // const id = { id: classId }
    // console.log(createClass, dropDowm, singleBox);
    const resl = { ...newClass, ...createClass, ...singleBox, ...dropDowm }
    this.setState({ newClass: resl }, () => {
      // console.log(this.state.newClass);

      // 将要更新提交给class页面，统一管理，便于维护
      PubSub.publish('createClass', this.state.newClass)
    })

  }





  render() {
    const classType = ['行政班', '教学班']
    const exitPermissions = ['允许退出', '通知班级管理员审核后退出', '禁止退出']
    const grade = ['年级', '一年级', '二年级', '三年级', '四年级']
    const section = ['学段', '小学', '初中', '高中', '大学']
    const { saveData, loseFocus, loseClassFocus } = this
    return (
      <div>
        <div className='class-create-totabar'>
          {/* 顶部导航 */}
          <div className="contenter-top-nav-bar">
            <div className="route-title">
              <h2 >我的班级</h2>
            </div>
            {/* <button className='creat-class'>创建班级</button> */}
            <BackClassBtn />
          </div>

          <div className="class-create-bar">
            <p className='create-tip'>尊敬的<span>老师(刘老师)</span>，请填写班级信息：</p>
            <form action="">

              {/* 输入学校 */}
              <div className='school'>
                <i>学校</i><input onBlur={loseFocus} ref={this.schoolRef} type="text" placeholder='请选择学校' />
                <div ref={this.schoolChildRef} className='school-tips school-true-tips school-tips-false '><div></div><p>请输入4-20位的中文或者5-50位的字母</p></div>
              </div>

              {/* 选择选段 */}
              <div className="section">
                <i><span>*</span>学段</i>
                <DropDowm data={section} pubName='sectionValue' liValue='section' />
              </div>

              {/* 选择年级 */}
              <div className="grade">
                <i><span>*</span>年级</i>
                <DropDowm data={grade} pubName='gradeValue' liValue='grade' />
              </div>

              {/* 输入班级名 */}
              <div className='class-name'>
                <i><span>*</span>班级名</i><input onBlur={loseClassFocus} ref={this.classRef} type="text" name="" id="" placeholder='请输入班级名称，限20字符，不支持输入_和%' />
                <div ref={this.classChildRef} className='course-tips course-tips-true course-tips-false'><div></div><p>限20字符，不支持输入_和%' </p></div>
              </div>

              {/* 选择类型 */}
              <div className='class-type'>
                <i>类型</i>
                <SingleBox data={classType} singleBoxName='classType' />
              </div>

              {/* 退出权限设置 */}
              <div className='exit-permissions'>
                <i>退出权限设置</i>
                <SingleBox data={exitPermissions} singleBoxName='exitPermissions' />
              </div>
              {/* 保存按钮 */}
              <button onClick={saveData}>保存</button>
            </form>
          </div>
        </div>
      </div>
    )
  }
}
