import React, { Component } from 'react'
import { Link, Route, Switch } from 'react-router-dom'
import PubSub from 'pubsub-js'

import ClassSynopsis from '../../../components/content/ClassSynopsis'
import Detail from './Detail'
import Create from '../../../containers/Create'


import './css/index.css'

export default class MyClass extends Component {


  state = {
    classes: [
      {
        id: '001',
        section: '高中-2013级',
        grade: '三年二班',
        headmaster: '田小雨',
        students: [
          { id: '0001001', name: '佩奇' },
          { id: '0001002', name: '乔治' },
          { id: '0001003', name: '老王' },
          { id: '0001004', name: '张三' },
        ],
        teachers: [
          { id: '0001001', name: '王朝' },
          { id: '0001002', name: '马汉' },
          { id: '0001003', name: '张龙' },
          { id: '0001004', name: '赵虎' },
        ],
        parents: [
          { id: '0001001', name: '刘德华' },
          { id: '0001002', name: '郭富城' },
          { id: '0001003', name: '黎明' },
          { id: '0001004', name: '张学友' },
        ]
      },
      {
        id: '002',
        section: '高中-2014级',
        grade: '三年二班', headmaster: '田小雨',
        students: [
          { id: '000101', name: '佩奇' },
          { id: '000102', name: '乔治' },
          { id: '000103', name: '老王' },
          { id: '000104', name: '张三' },
        ],
        teachers: [
          { id: '0001001', name: '王朝' },
          { id: '0001002', name: '马汉' },
          { id: '0001003', name: '张龙' },
          { id: '0001004', name: '赵虎' },
        ],
        parents: [
          { id: '0001001', name: '刘德华' },
          { id: '0001002', name: '郭富城' },
          { id: '0001003', name: '黎明' },
          { id: '0001004', name: '张学友' },
        ]
      },
      {
        id: '003',
        section: '高中-2015级',
        grade: '三年二班',
        headmaster: '田小雨',
        students: [
          { id: '000106', name: '佩奇' },
          { id: '000107', name: '乔治' },
          { id: '000110', name: '老王' },
          { id: '000111', name: '张三' },
        ],
        teachers: [
          { id: '0001001', name: '王朝' },
          { id: '0001002', name: '马汉' },
          { id: '0001003', name: '张龙' },
          { id: '0001004', name: '赵虎' },
        ],
        parents: [
          { id: '0001001', name: '刘德华' },
          { id: '0001002', name: '郭富城' },
          { id: '0001003', name: '黎明' },
          { id: '0001004', name: '张学友' },
        ]
      },
      {
        id: '004',
        section: '高中-2016级',
        grade: '三年二班', headmaster: '田小雨',
        students: [
          { id: '000112', name: '佩奇' },
          { id: '000113', name: '乔治' },
          { id: '000114', name: '老王' },
          { id: '000115', name: '张三' },
        ],
        teachers: [
          { id: '0001001', name: '王朝' },
          { id: '0001002', name: '马汉' },
          { id: '0001003', name: '张龙' },
          { id: '0001004', name: '赵虎' },
        ],
        parents: [
          { id: '0001001', name: '刘德华' },
          { id: '0001002', name: '郭富城' },
          { id: '0001003', name: '黎明' },
          { id: '0001004', name: '张学友' },
        ]
      },
    ]
  }

  componentDidMount() {

    // console.log('....');
    this.createClassToken = PubSub.subscribe('createClass', (a, data) => {
      const { state: { classes } } = this
      console.log(classes);
      console.log(data);
      const newClass = [...classes, data]
      console.log(newClass);
      this.setState({ classes: newClass }, () => {
        PubSub.publish('classMessage', this.state.classes)
      })
    })
  }

  componentWillUnmount() {
    PubSub.unsubscribe(this.createClassToken)
  }


  render() {
    // console.log(this);

    const { classes } = this.state

    return (
      <div>
        {/* 顶部导航 */}
        <div className="contenter-top-nav-bar">
          <div className="route-title">
            <h2 >我的班级</h2>
          </div>
          <Link to='/class/create' ><button className='creat-class'>创建班级</button></Link>
          {/* <button className='join-class'>加入班级</button> */}
        </div>
        <div className="class-totall-bar">
          <h3>行政班是为学生管理和教学管理而建设的班级</h3>


          <div className="class-bar">
            {
              classes.map((classesObj) => {
                return (
                  <Link to={`/class/detail/${classesObj.id}`} key={classesObj.id}  >
                    < ClassSynopsis  {...classesObj} />
                  </Link>
                )
              })

            }
          </div>

          <h3>教学班是根据教学要求而设置的班级</h3>
          <div className="class-bar">
            {
              classes.map((classesObj) => {
                return (
                  <Link to={`/class/detail/${classesObj.id}`} key={classesObj.id}  >
                    < ClassSynopsis  {...classesObj} />
                  </Link>
                )
              })

            }

          </div>
        </div>
        <Switch>
          <Route path='/class/create' component={Create}></Route>
          <Route path={`/class/detail/:id`} component={Detail}></Route>
        </Switch>
      </div>
    )
  }
}
