import { connect } from 'react-redux'

import SingleBox from '../../components/commom/SingleBox'
import { singleBoxFocusValue } from '../../redux/actions/singleBox'

// function a(state) {
//   // console.log(state);
//   return {
//     singleBox: state.SingleBox
//   }
// }
function a(state) {
  // console.log(state);
  return {
    singleBox: state.SingleBox
  }
}
function b(dispatch) {
  return {
    singleBoxFocusValue: (data) => { dispatch(singleBoxFocusValue(data)) }
  }
}

export default connect(a, b)(SingleBox)
