import React, { Component } from 'react'

import './css/index.css'

export default class DropDowm extends Component {

  state = {
    title: '请选择年级'
  }

  componentDidMount() {
    // const {   } = this
    const data = this.props.data || this.props.data1
    console.log(data[0]);
    this.setState({ title: data[0] })

  }

  showDRopDowmList = (event) => {
    // console.log(event.target.childNodes[1]);s
    // console.log(this.dropDowmList);
    const { childNodes } = event.target
    this.toggleClass(this.dropDowmList, 'dropdown-ul-active')
    this.toggleClass(childNodes[1], 'dropdown-icon-active')
    // childNodes[1]
  }

  showDRopDowmLi = (index) => {

    return () => {
      const { toggleClass, dropDowmInpt: { childNodes }, dropDowmList, dropDowmList: { children } } = this
      const data = this.props.data || this.props.data1
      console.log(this);
      toggleClass(dropDowmList, 'dropdown-ul-active')
      toggleClass(childNodes[1], 'dropdown-icon-active')
      console.log(childNodes[0]);
      console.log(children);
      data.map((value, index1) => {
        if (index1 === index) {
          console.log(children[index1].childNodes[0], value);
          const title = children[index1].childNodes[0]
          console.log(title);
          return this.setState({ title: 'nihao' })
        }
      })
    }
  }

  // 添加class
  addClass = (obj, cn) => {
    // 检查是否有cn
    if (!this.hasClass(obj, cn)) {
      obj.className += " " + cn
    }
  }
  // 检查是否有cn
  hasClass = (obj, cn) => {
    const reg = new RegExp("\\b" + cn + "\\b")
    return reg.test(obj.className);
  }
  // 删除class
  deleClass = (obj, cn) => {
    // 查找是否存在
    const reg1 = new RegExp("\\b" + cn + "\\b", "ig");
    // console.log(reg1);
    //删除class
    obj.className = obj.className.replace(reg1, "");
  }
  /*
   * toggleClass 可以用来切换一个类
   * 	如果元素中具有该类，则删除
   * 	如果元素中没有该类，则添加
   */
  toggleClass = (obj, cn) => {
    //判断obj中是否含有cn
    if (this.hasClass(obj, cn)) {
      //有，则删除
      this.deleClass(obj, cn);
    } else {
      //没有，则添加
      this.addClass(obj, cn);
    }
  }
  render() {
    const data = this.props.data || this.props.data1
    const { showDRopDowmLi, showDRopDowmList, state: { title } } = this
    // console.log(this.props.data1);
    return (
      <div className="dropdown">
        <p ref={e => this.dropDowmInpt = e} onClick={showDRopDowmList}>`请选择{title}`<span className='dropdown-icon'></span></p>
        <ul className='dropdown-ul' ref={e => this.dropDowmList = e}>
          {
            data.map((value, index) => {

              return index !== 0 ? < li onClick={showDRopDowmLi(index)} key={index} > {value}</li> : ''
            })
          }

        </ul>
      </div >
    )
  }
}
