// const newClass = { school: '', section: '', grade: '', course: '', type: "", exitPermissions: "" },
function createClassReducer(praState = newClass, action) {
  const { type, data } = action
  switch (type) {
    case 'setSchool':
      return { ...praState, school: data }
    case 'setGrade':
      return { ...praState, grade: data }

    default:
      return praState
  }
}

export default createClassReducer
